import React, { useState } from 'react';
import './Contact.css';
import linkedin from './assets/linkedin.png';
import email from './assets/email.png';

const Contact = () => {

    return (
        <section className="contact">
            <div className="contact-container">
                <h2>Contact Me!</h2>
                <div className="external-links">
                    <img className="logo" src={email} alt="email picture"/><p>mendoza.a01@outlook.com</p>
                    <a href="https://linkedin.com/in/antonio-mendoza01/" target="_blank"><img className="logo" src={linkedin} alt="linkedin button"/></a>
                </div>
                <h3>Or send an email</h3>
                <div className="form-container">
                    <form action="https://formsubmit.co/mendoza.a01@outlook.com" method="POST">
                        <input type="text" name="name" placeholder="Your Name" required />
                        <input type="email" name="email" placeholder="Your Email" required />
                        <input type="text" name="message" placeholder="Your Message" required />
                        <button type="submit" className="send-btn">Send</button>
                    </form>
                </div>
            </div>
        </section>
    );
};

export default Contact;
