import React, { useRef } from 'react';
import './Projects.css';
import github from './assets/github.png';
import uga from './assets/uga.png';

const Projects = () => {
    const carouselRef = useRef(null);

    const scrollLeft = () => {
        carouselRef.current.scrollBy({ left: -300, behavior: 'smooth '});
    };

    const scrollRight = () => {
        carouselRef.current.scrollBy({ left: 300, behavior: 'smooth '});
    };

    const projects = [
        {
            title: 'Research in Deep Leakage From Gradients',
            img: {uga},
            description: 'Language: Python \n Libraries: PyTorch, TensorFlow, TorchVision, Numpy, and Matplotlib',
            link:'https://github.com/amendoza09/gradient_inversion_attack',
        },
        {
            title: 'LevelUp Fitness',
            description: 'Langauge: JavaScript \n Tools: React, Node.JS, MongoDB',
            link: 'link',
        },
        {
            title: 'Splunk Dashbaord',
            description: 'Splunk',
            link: 'link',
        },
        {
            title: 'Bulldog Buddies',
            description: 'Language: JavaScript \n Tools: React, Node.JS, Google Firebase',
            link: 'link',
        },
    ];

    return (
    <section className="projects">
        <h2>My Projects</h2>
        <div className="carousel-container">
            <div className="project-carousel" ref={carouselRef}>
                {projects.map((project, index) => (
                    <div key={index} className="project-card">
                        <h3 className="proj-title">{project.title}</h3>
                        <div> 
                            <img src={project.img} />
                        </div>
                        <div className="proj-description">
                            <p>{project.description}</p>
                        </div>
                        <a href={project.link} target="_blank" rel="noopener noreferrer"><img src={github} className="logo" alt="github-logo"/></a>
                    </div> 
                ))}
            </div>
        </div>
    </section>
    );
};

export default Projects;
