import React, { useState } from 'react';
import './Contact.css';
import linkedin from './assets/linkedin.png';
import email from './assets/email.png';
import emailjs from 'emailjs-com';

const Contact = () => {
    const [formData, setFormData] = useState ({
        name: '',
        email: '',
        message: '',
    });

    const [isSent, setIsSent] = useState(false);
    const [error, setError] = useState(null);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData((prevData) => ({
            ...prevData,
            [name]: value,
        }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();

        console.log("Form Submitted Successfully", formData);
        alert("Thank you for contact me!");
        setFormData({name: '', email: '', message: ''});
    }

    return (
        <section className="contact">
            <div className="contact-container">
                <h2>Contact Me!</h2>
                <div className="external-links">
                    <img className="logo" src={email} alt="email picture"/><p>mendoza.a01@outlook.com</p>
                    <a href="https://linkedin.com/in/antonio-mendoza01/" target="_blank"><img className="logo" src={linkedin} alt="linkedin button"/></a>
                </div>
                <h3>Or send an email</h3>
                <div className="form-container">
                    <form onSubmit={handleSubmit}>
                        <input type="text" id="name" name="name" value={formData.name} onChange={handleChange} placeholder="Your Name" required />
                        <input type="email" name="email" value={formData.email} onChange={handleChange} placeholder="Your Email" required />
                        <input type="text" name="message" value={formData.messgae} onChange={handleChange} placeholder="Your Message" required />
                        <button type="submit" className="send-btn">Send</button>
                    </form>
                </div>
            </div>
        </section>
    );
};

export default Contact;
