import React, { useRef } from 'react';
import './Projects.css';

const Projects = () => {
    const carouselRef = useRef(null);

    const scrollLeft = () => {
        carouselRef.current.scrollBy({ left: -300, behavior: 'smooth '});
    };

    const scrollRight = () => {
        carouselRef.current.scrollBy({ left: 300, behavior: 'smooth '});
    }

    const projects = [
        {
            title: 'Project 1',
            description: 'description',
            link: 'link',
        },
        {
            title: 'Project 2',
            description: 'description',
            link: 'link',
        },
        {
            title: 'Project 3',
            description: 'description',
            link: 'link',
        },
    ];

    return (
    <section className="projects">
        <h2>My Projects</h2>
        <div className="carousell-container">
            <button className="scroll-btn" onClick={scrollLeft}left></button>
            <div className="projects-carousel" ref={carouselRef}>
                {projects.map((project, index) => (
                <div key={index} className="project-card">
                    <h3>{project.title}</h3>
                    <p>{project.description}</p>
                    <a href={project.link} target="_blank" rel="noopener noreference" >code</a>
                </div> 
                ))}
            </div>
            <button className="scroll-btn" onClick={scrollRight}>Right</button>
        </div>
    </section>
    );
};

export default Projects;
