import React, { useRef } from 'react';
import './Projects.css';
import github from './assets/github.png';
import uga from './assets/uga.png'
import levelupFitnessLogo from './assets/LevelUp-Fitness-square.png'
import splunkLogo from './assets/splunk-logo.png'
import defaultImg from './assets/Personal-web-picture.png';

const Projects = () => {

    const projects = [
        {
            title: 'Deep Leakage From Gradients',
            img: uga,
            language: 'Langauge: Python',
            libraries: 'Libraries: PyTorch, TensorFlow, TorchVision, Numpy, and Matplotlib',
            description: "Simulated a gradient inversion attack on the CIFAR-10 dataset. Used ResNet-18 architecture to carry out the attack and observe the beahvior of gatehring gradient information.",
            link:'https://github.com/amendoza09/gradient_inversion_attack',
        },
        {
            title: 'LevelUp Fitness',
            img: levelupFitnessLogo,
            language: 'Langauge: JavaScript',
            libraries:'Tools: React, Node.JS, MongoDB',
            description: 'Users can create an account and track their progress on their fitness journey and share workouts for other users to see. As you wokrout, you can level up your charcter and unlock stronger ones as you progress in your personal journey.',
            link: 'link',
        },
        {
            title: 'Splunk Dashbaord',
            img: splunkLogo,
            language: '',
            libraries:'Tools: Splunk',
            description: 'Splunk',
            link: 'link',
        },
        {
            title: 'Bulldog Buddies',
            img: defaultImg,
            language: 'Langauge: JavaScript',
            libraries:'Tools: React, Node.JS, Google Firebase',
            description: '',
            link: 'link',
        },
        {
            title: 'Command Shell',
            img: defaultImg,
            language: 'Langauge: C',
            libraries:'',
            description: 'Low Level I/O ',
            link: 'link',
        },
        {
            title: 'Chess Game',
            img: defaultImg,
            language: 'Langauge: Python',
            libraries:'Libraries: PyGame',
            description: '',
            link: 'link',
        },
        {
            title: 'Personal Website',
            img: defaultImg,
            language: 'Langauge: JavaScript',
            libraries:'Libraries: React',
            description: '',
            link: 'link',
        },
    ];

    return (
    <section className="projects transition-fade">
        <h2 className="pg-title">My Projects</h2>
        <div className="carousel-container">
            <div className="project-carousel">
                {projects.map((project, index) => (
                    <div key={index} className="project-card">
                        <h3 className="proj-title">{project.title}</h3>
                        <div className="image-container"> 
                            <img className="proj-image" src={project.img} alt="project picture"/>
                            <div className="proj-description">
                                <p>{project.language}</p>
                                <p>{project.libraries}</p>
                                <p>{project.description}</p>
                            </div>
                        </div>
                        <a href={project.link} target="_blank" rel="noopener noreferrer"><img src={github} className="logo" alt="github-logo"/></a>
                    </div> 
                ))}
            </div>
        </div>
    </section>
    );
};

export default Projects;
